import React, { createContext, useState } from 'react'

const libro = [{
    idBook: 11,
    bookTitle: 'Don quijote',
    bookAuthor: 'Miguel Cervantes',
    bookCover: 'data:image/jpeg;base64,',
    bookIntro: 'Don quijote tremendo libro',
    bookCompleted: true,
    bookReview: 'Don quijote es un loco que pa que expllicarte',
},
{
    idBook: 155,
    bookTitle: 'Padre Rico',
    bookAuthor: 'Takeshi',
    bookCover: 'data:image/jpeg;base64,',
    bookIntro: 'La tiza pa emprender',
    bookCompleted: true,
    bookReview: 'Un libro para aprender a tener buena mentalidad al exito',
},
{
    idBook: 200,
    bookTitle: 'Edad Oro',
    bookAuthor: 'Jose Marti',
    bookCover: 'data:image/jpeg;base64,',
    bookIntro: 'El frentu tirando sus tayas',
    bookCompleted: true,
    bookReview: 'Un libro para nios que tiro marti pa inflar un poco',
},
]

export const GlobalContextLibrary = createContext();

const UseLibrary = ({ children }) => {
    const [books, setBook] = useState(libro)

    const getBookId = (idBook) => {
        let book = [];
        book = books.filter((bookX) => (bookX.idBook == idBook))
        return book[0]
    }


    return <GlobalContextLibrary.Provider value={{ books, setBook, getBookId }}>
        {children}
    </GlobalContextLibrary.Provider>

}

export default UseLibrary